import React, { useContext, useState } from "react";
import { auth, db } from "../firebase/firebase-config";
import { useEffect } from "react";
import firebase from "firebase";

const AuthContext = React.createContext();

export function useAuth() {
  return useContext(AuthContext);
}

export function AuthProvider({ children }) {
  const [currentUser, setCurrentUser] = useState();
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const unsubscribe = auth.onAuthStateChanged((user) => {
      setCurrentUser(user);
      setLoading(false);
    });
    return unsubscribe;
  }, []);

  async function signUp(email, password) {
    const userCredential = await firebase
      .auth()
      .createUserWithEmailAndPassword(email, password);
    const uid = userCredential.user.uid; //Pulls user ID out of Result
    //Build a reference to their per-user document in the
    //users collection
    const userDocRef = db.collection("users").doc(uid);
    // Add some initial data to it
    await userDocRef.set({
      createdAt: db.FieldValue.serverTimestamp(),
      propogators_Connected: 0,
    });
  }

  // const authOnCreate = auth.user().onCreate(async (user) => {
  //   console.log(`Creating document for user ${user.uid}`);
  //   await db.collection("users").doc(user.uid).set({
  //     createdAt: db.FieldValue.serverTimestamp(),
  //     credits: 0,
  //   });
  // });

  const logIn = (email, password) => {
    auth.signInWithEmailAndPassword(email, password);
  };

  function logOut() {
    return auth.signOut();
  }

  const value = {
    currentUser,
    signUp,
    logIn,
    logOut,
    loading,
  };

  return (
    <AuthContext.Provider value={value}>
      {!loading && children}
    </AuthContext.Provider>
  );
}
